changeSet.createProcedure:
CREATE OR REPLACE TRIGGER trg_cmpt_update_assn_names AFTER UPDATE OF cm_complaint_number ON acm_complaint FOR EACH ROW BEGIN
pkg_update_association_names.update_association_names(:NEW.cm_complaint_id, 'COMPLAINT', :NEW.cm_complaint_number);
END trg_cmpt_update_assn_names;

CREATE OR REPLACE TRIGGER trg_cmpt_delete
AFTER DELETE ON acm_complaint
FOR EACH ROW
BEGIN
    pkg_update_association_names.remove_associations(:OLD.cm_complaint_id, 'COMPLAINT');
END trg_cmpt_delete;
